#include <stdio.h>
#include <stdbool.h>

#define MAX_SIZE 10005

typedef struct {
    char arr[MAX_SIZE];
    int top;
} Stack;

void initialize(Stack* stack) {
    stack->top = -1;
}

bool empty(Stack* stack) {
    return stack->top == -1;
}

int size(Stack* stack) {
    return stack->top + 1;
}

char top(Stack* stack) {
    if (empty(stack)) {
        return -1;
    }
    return stack->arr[stack->top];
}

void push(Stack* stack, char value) {
    if (stack->top >= MAX_SIZE - 1) {
        printf("Stack is full\n");
        return;
    }
    stack->arr[++stack->top] = value;
}

void printStack(Stack* stack){
    printf("Stack : ");
    for (int i=0; i<stack->top+1; i++){
        printf("%c ", stack->arr[i]);
    }
}

void pop(Stack* stack) {
    if (empty(stack)) {
        printf("Stack is empty\n");
        return;
    }
    stack->top--;
}

bool isValid(char * s){
    Stack stack;
    initialize(&stack);
    int length = 0;
    while (1){
        // printStack(&stack);
        char curr = s[length];
        if (curr == '\0') break;
        if (empty(&stack) == 1){
            if (curr == '(' || curr == '{' || curr == '['){
                push(&stack, curr);
                }
            else {
                return 0;
            }
        }
        else if ((curr == ')' && top(&stack) == '(') ||
            (curr == '}' && top(&stack) == '{') ||
            (curr == ']' && top(&stack) == '[')){
                pop(&stack);
            }
        else if ((curr == ']' && top(&stack) == '(') ||
            (curr == '}' && top(&stack) == '(') ||
            (curr == ']' && top(&stack) == '{') ||
            (curr == ')' && top(&stack) == '{') ||
            (curr == ')' && top(&stack) == '[') ||
            (curr == '}' && top(&stack) == '[')){
                return 0;
            }
        else {
                push(&stack, s[length]);
            }
        length ++;
    }
    // printf("%d\n", size(&stack) );
    if (size(&stack) != 0) return 0;
    else return 1;
}


